CREATE DATABASE [LoreStore];
GO

USE [LoreStore];
GO

CREATE TABLE [dbo].[Book](
	[id] int PRIMARY KEY IDENTITY(1, 1),
	[title] [nvarchar](300) NOT NULL,
	[authorLastName] [nvarchar](100) NULL,
	[authorFirstName] [nvarchar](50) NULL,
	[datePublished] [date] NOT NULL,
	[description] [nvarchar](100) NOT NULL,
	[isFiction] [bit] NOT NULL,
	[subGenre] [nvarchar](50) NOT NULL,
	[price] [float] NOT NULL,
	[inventoryQuantity] [tinyint] NOT NULL,
	[photoUrl] [nvarchar](1) NULL
) ON [PRIMARY]
GO

CREATE TABLE [dbo].[User] (
  [id] int PRIMARY KEY IDENTITY(1, 1),
  [email] varchar(254),
  [firstName] varchar(40),
  [lastName] varchar(40),
  [username] varchar(20),
  [address1] varchar(254),
  [address2] varchar(254),
  [city] varchar(40),
  [state] varchar(2),
  [zip] varchar(10),
  [isAdmin] bit
)
GO
​
CREATE TABLE [dbo].[PaymentMethod] (
  [id] int PRIMARY KEY IDENTITY(1, 1),
  [userId] int,
  [firstName] varchar(40),
  [lastName] varchar(40),
  [cardNumber] varchar(16),
  [expirationMonth] varchar(2),
  [expirationYear] varchar(2),
  [cvv] varchar(4)
)
GO
​
CREATE TABLE [dbo].[Order] (
  [id] int PRIMARY KEY IDENTITY(1, 1),
  [userId] int,
  [paymentMethodId] int,
  [total] float,
  [orderDate] datetime,
  [isComplete] bit,
  [status] varchar(10)
)
GO
​
CREATE TABLE [dbo].[OrderDetail] (
  [id] int PRIMARY KEY IDENTITY(1, 1),
  [orderId] int,
  [bookId] int,
  [quantity] int,
  [unitPrice] float,
)
GO

